                cpt_errors++;

                $( this ).find( 'div:eq(0)' ).addClass('has-error');
                $( this ).find( 'div:eq(0)' ).find( 'div:eq(0)' ).text('This value is too short. It should have 2 characters or more.');
                $( this ).find( 'div:eq(0)' ).find( 'div:eq(0)' ).collapse('show');
